import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "../signup.css";
import { Link, useNavigate } from "react-router-dom";
export default function Signup({ onLogin }) {
  const [name, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [errors, setErrors] = useState([]);
  const navigate = useNavigate();
  const showToastMessage = () => {
    toast.success("Signup Successfull!", {
      position: toast.POSITION.TOP_CENTER,
      className: 'toast-message'
    });
  };
  const toastMessage = () => {
    toast.success("Password don't match !", {
      position: toast.POSITION.TOP_CENTER,
    });
  };
  const errorMessage = () => {
    toast.success("Username/Email already taken !", {
      position: toast.POSITION.TOP_CENTER,
    });
  };
  function handleSubmit(e) {
    e.preventDefault();
    setErrors([]);
    fetch("/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        fullName: name,
        email,
        username,
        password,
        password_confirmation: passwordConfirmation,
      }),
    }).then((r) => {
      if (r.ok) {
        r.json().then((user) => onLogin(user));
        showToastMessage();
        setTimeout(() => {
          navigate("/login");
        }, 2000);
      } else {
        r.json().then((err) => {
          setErrors(err.errors);
          console.log(errors);
          if (errors === "Password confirmation doesn't match Password") {
            toastMessage();
          } else if (errors) {
            errorMessage();
          }
        });
      }
    });
  }
  return (
    <div className="signup">
      <ToastContainer />
      <span className="signuptittle">Sign Up Here</span>
      <form onSubmit={handleSubmit} className="registerform">
        <label>Full Name</label>
        <input
          onChange={(e) => setFullName(e.target.value)}
          id="username"
          className="registerinput"
          type="text"
          value={name}
          placeholder="Enter your username.."
        />
        <label>Email</label>
        <input
          onChange={(e) => setEmail(e.target.value)}
          id="email"
          className="registerinput"
          type="email"
          value={email}
          placeholder="Enter your email..."
        />
        <label>username</label>
        <input
          onChange={(e) => setUsername(e.target.value)}
          id="number"
          className="registerinput"
          type="text"
          placeholder="Enter your username..."
        />
        {/* <label>Location</label>
        <input
          id="text"
          className="registerinput"
          type="text"
          placeholder="Enter your location..."
        /> */}
        <label>Password</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          id="password"
          className="registerinput"
          type="password"
          value={password}
          placeholder="Enter your password..."
        />
        <label>Confirmation Password</label>
        <input
          onChange={(e) => setPasswordConfirmation(e.target.value)}
          id="text"
          className="registerinput"
          type="password"
          value={passwordConfirmation}
          placeholder="Confirm password..."
        />
        <button className="loginbutton">Sign Up</button>
        <p>
          Already Signed Up?
          <Link to="/login" style={{ color: "blue", paddingLeft: "10px" }}>
            Login Here
          </Link>
        </p>
      </form>
    </div>
  );
}
